const names = [
  "Alice Johnson", "Brian Smith", "Catherine Lee", "David Chen",
  "Ella Martinez", "Frank Williams", "Grace Kim", "Henry Patel",
  "Isabella Brown", "Jack Thompson", "Karen Lopez", "Leo Garcia"
];

const issues = [
  "Hi, my checkout failed twice last night. The card is valid and I tried different browsers. Can you confirm if there was an outage?",
  "Hello support, I was charged twice for order #A1045. The bank shows two pending transactions. Please refund the duplicate.",
  "Good morning, I can't reset my password. The reset link says it’s expired within minutes. Any workaround?",
  "Hi team, our integration stopped syncing at 3pm. The logs mention 401 errors. Did our credentials expire?",
  "Hello, I updated my billing address but the invoice still shows the old one. Can you regenerate it?",
  "Support, the mobile app crashes on launch since the last update. I reinstalled it twice. Any fix timeline?",
  "Hi there, I need to change the email on my account. The UI blocks it saying 'contact support'. What info do you need?",
  "Greetings, I submitted a cancellation last week but I was billed again today. Please confirm the subscription status.",
  "Hey, my teammate can’t access shared projects anymore. Permissions look correct on our side. Could this be a role issue?",
  "Hello, where can I download previous invoices for the full year? The portal only shows the last three months.",
  "Hi support, the webhook delivery is delayed by several minutes today. We’re processing purchases in real time. Any incident?",
  "Good afternoon, I need a copy of our DPA and security questionnaire for procurement. Whom should I contact?"
];

const messages = [];

for (let i = 0; i < 12; i++) {
  const name = names[i];
  const first = name.split(" ")[0].toLowerCase();
  const last = name.split(" ")[1].toLowerCase();
  const email = `${first}.${last}@examplemail.com`;

  messages.push({
    json: {
      ticket_id: 1000 + i,
      from: email,
      customer_name: name,
      email_text: issues[i]
    }
  });
}

return messages;
