// Acme Logistics – incident sample generator (12 items)
// Produces mostly-valid items plus some with missing required fields
// so you can exercise your "Check" and "Validate" steps.

// ---------- helpers ----------
const rand = (arr) => arr[Math.floor(Math.random() * arr.length)];
const bool = (p = 0.5) => Math.random() < p;
const int = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min;
const isoOffsetMins = (mins) => new Date(Date.now() + mins * 60_000).toISOString();
const pad = (n, w = 4) => String(n).padStart(w, '0');

// ---------- lookup data ----------
const cities = ['Austin','Denver','Atlanta','Seattle','Phoenix','Boston','Miami','Portland'];
const streets = ['Elm St','Maple Ave','2nd St','Cedar Rd','Oak Blvd','Pine Way','Market St','Lakeview Dr'];
const carriers = ['UPS','FedEx','DHL','USPS'];
const channels = ['email','webform','phone','system'];
const firstNames = ['Avery','Jordan','Taylor','Morgan','Riley','Casey','Harper','Quinn'];
const lastNames  = ['Nguyen','Garcia','Patel','Miller','Davis','Kim','Lopez','Johnson'];
const products   = ['Laptop','Headphones','Groceries','Vitamins','Coffee Beans','Desk Lamp','Monitor','Camera'];

const hard = [
  'Perishables spoiled; van refrigeration failed and ETA slipped 3 hours.',
  'Box crushed on arrival; two units missing; customer very upset.',
  'Address mismatch; courier left at wrong door; urgent retrieval needed.',
  'Trailer breakdown overnight; shipment delayed, client escalation pending.',
  'Courier lost one unit from the set; replacement required ASAP.'
];

const soft = [
  'Slight scuff on outer box; item works fine.',
  'Driver 20 minutes late; customer accepted delivery.',
  'Label smudged; reprint needed for return.',
  'Customer requested weekday delivery; rescheduled.',
  'Minor dent on outer box; contents verified OK.'
];

// ---------- generator ----------
const items = [];
for (let i = 1; i <= 12; i++) {
  const severe = bool(0.45);
  const name = `${rand(firstNames)} ${rand(lastNames)}`;
  const city = rand(cities);
  const street = `${int(100, 999)} ${rand(streets)}`;
  const product = rand(products);
  const qty = int(1, 4);

  const record = {
    order_id: `ORD-${pad(1000 + i)}`,
    email: `ops+${i}@acme-logistics.example`,
    customer_name: name,
    phone: `+1-555-${int(100, 999)}-${int(1000, 9999)}`,
    channel: rand(channels),

    // shipping/address
    address_line1: street,
    city,
    postal_code: String(int(10000, 99999)),
    country: 'US',
    carrier: rand(carriers),

    // items
    product,
    sku: `SKU-${int(10000, 99999)}`,
    quantity: qty,
    value_usd: Number((int(1500, 45000) / 100).toFixed(2)), // $15–$450

    fragile: bool(0.3),
    temp_sensitive: bool(0.2),

    // timing
    created_at: isoOffsetMins(-int(0, 720)),     // sometime in last 12 hours
    eta: isoOffsetMins(int(60, 60 * 24 * 3)),    // 1 hour–3 days in future

    // incident text
    message: severe ? rand(hard) : rand(soft),

    // extra metadata you can normalize or drop later
    tags: severe ? ['incident','customer_impact'] : ['incident'],
  };

  items.push({ json: record });
}

// ---------- enforce exactly 2 records with blank message or order_id ----------
const invalidIndexes = new Set();
// pick 2 distinct random indices from 0..items.length-1
while (invalidIndexes.size < 2) {
  invalidIndexes.add(int(0, items.length - 1));
}

for (const idx of invalidIndexes) {
  const rec = items[idx].json;

  // randomly choose which field to blank on this record
  if (bool(0.5)) {
    rec.message = '';      // blank message
  } else {
    rec.order_id = '';     // blank order_id
  }
}

return items;
